function MapredClient(socket, opt)
{
	var $this = this;
	this.free = true;
	this.socket = socket;
	this.opt = opt || {messageCallback: this.defalutCallback, log: this.defalutCallback, resultCallback: this.defalutCallback};
	this.gc = setInterval(function(){if ($this.isFree()) $this.socket.emit('task', {free: $this.free});}, 200);
	this.init();
	this.onMap();
	this.onReduce();
	this.onResult();
}
MapredClient.prototype.getTask = function()
{
	if (this.isFree()) this.socket.emit('task', {free: this.free});
}
MapredClient.prototype.isFree = function()
{
	return this.free;
}
MapredClient.prototype.defalutCallback = function(data)
{
	console && console.log(data);
}
MapredClient.prototype.init = function()
{
	this.socket.on('ready', function(data){document.title = data;});
	this.socket.on('message', this.opt.messageCallback || this.defalutCallback);

}
MapredClient.prototype.log = function()
{
	$this.opt.log && $this.opt.log(data) || $this.defalutCallback(data);
}
MapredClient.prototype.onMap = function()
{
	this.free = false;
	var $this = this;
	this.socket.on('map', function (data) {
	  try {
			eval('var map = ' + data.map + ';');
			var intermediate = [], groups = null;
			
			data.datas.forEach(function(elem, index){
				var key = elem[0], value = elem[1];
				intermediate = intermediate.concat(map(key, value));
			});
			intermediate.sort();
			
			groups = intermediate.reduce(function(res, current){
				var group = res[current[0]] || [];
				group.push(current[1]);
				res[current[0]] = group;
				return res; 
			}, {});
			delete data['datas'];
			data.groups = groups;
			socket.emit('flod', data);
			this.free = true;
		} catch(err) {
			$this.opt.log && $this.opt.log("Error name: " + err.name + "Error message: " + err.message)
		}

	});
};
MapredClient.prototype.onReduce = function()
{
	this.free = false;
	var $this = this;
	socket.on('reduce', function (data) {
		try{
			eval('var reduce = ' + data.reduce + ';');
			var groups = data.datas;
			
			for(var k in groups){
				groups[k] = reduce(k, groups[k]);
			}
			data.groups = groups;
			socket.emit('result', data);
			this.free = true;
		} catch(err) {
			$this.opt.log && $this.opt.log("Error name: " + err.name + "Error message: " + err.message)
		}
	});
};
MapredClient.prototype.onResult = function()
{
	socket.on('result', this.opt.resultCallback || this.defalutCallback)
};
